const AddNewUser = () => {
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);

    const [user, setUser] = useState(null);
    const [pass, setPass] = useState(null);
    const [email, setEmail] = useState(null);
    const [org, setOrg] = useState(null);
    const [address, setAddress] = useState(null);
    const [city, setCity] = useState(null);
    const [locstate, setLocState] = useState(null);
    const [country, setCountry] = useState(null);
    const [postal, setPostal] = useState(null);

    let userDetails = [
        {username: {user}},
        {password: {pass}},
        {email: {email}},
        {organization: {org}},
        {address: {address}},
        {city: {city}},
        {state: {locstate}},
        {country: {country}},
        {postalcode: {postal}},
    ];

    //!!!

    const handleSubmit = async(event) => {
        console.log("submit pressed");
        event.preventDefault();
        const settings = {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                user,
                pass,
                email,
                org,
                address,
                city,
                locstate,
                country,
                postal,
            }),
        };
        try{
            const fetchResponse = await fetch('http://127.0.0.1:5000/addNewUser/', settings);
            const data = await fetchResponse.json();
            return data;
        } catch (e){
            return e;
        }
    }

    if (isLoading) return <p>Loading...</p>;
    if (error) return <p>Error: {error}</p>;

    return(
        <div>
        <form method="post" action="/post" onSubmit={handleSubmit}>
            <TextField id="user" label="Username:" onChange={(e)=>setUser(e.target.value)}/>
            <TextField id="pass" label="Password:" onChange={(e)=>setPass(e.target.value)}/>
            <TextField id="email" label="Email:" onChange={(e)=>setEmail(e.target.value)}/>
            <TextField id="org" label="Organization:" onChange={(e)=>setOrg(e.target.value)}/>
            <TextField id="address" label="Address:" onChange={(e)=>setAddress(e.target.value)}/>
            <TextField id="city" label="City:" onChange={(e)=>setCity(e.target.value)}/>
            <TextField id="locstate" label="State:" onChange={(e)=>setLocState(e.target.value)}/>
            <TextField id="country" label="Country:" onChange={(e)=>setCountry(e.target.value)}/>
            <TextField id="postal" label="Postalcode:" onChange={(e)=>setPostal(e.target.value)}/>
            <input type="submit" className="btn" value="Submit"/>
        </form>
        </div>
        );
}

const AddUser = () => {
    const [addUsr, setAddUsr] = useState(false);

    const handleUserClick = () => {
        setAddUsr(!addUsr);
        console.log("add user button pressed");
    }

    return(
    <div>
        <button onClick={handleUserClick} type="button">Add User</button>
        {addUsr && <AddNewUser />}
    </div>
    );
}


const App = () => {
    const [users, setUsers] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);
    //const [userAdded, setUserAdded] = useState(false);

    useEffect(() => {
        const fetchUsers = async() => {
            try{
                const response = await fetch("http://127.0.0.1:5000/userlist");

                if (!response.ok) {
                    throw new Error('failed to fetch users');
                }
                const data = await response.json();
                console.log(data);
                setUsers(data);
            } catch (err) {
                setError(err.message);
            } finally {
                setIsLoading(false);
            }
        };

        fetchUsers();
    }, []);


    if (isLoading) return <p>Loading...</p>
    if (error) return <p>Error: {error}</p>;

    return (
        <div>
        <AddUser />
        <UserList users={users}/>
        </div>
    );
};

export default App;
