import "./App.css"
import React from "react";
import {useEffect, useState} from "react";
import axios from "axios";

const SaveUserDetails = ({id, info_type}) =>{
    const [info, setInfo] = useState("");

      const handleSubmit = async(event) => {
        event.preventDefault();
        //alert('entered: ${email}');
        console.log("info is " + JSON.stringify({info}));
        console.log("info type is " + info_type);
        console.log(JSON.stringify({"id": id, info_type, "info": info}));
        const settings = {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                "id": id,
                info_type,
                "info": info,
            }),
        };
        try{
            const fetchResponse = await fetch('http://127.0.0.1:5000/saveUserDetails/', settings);
            const data = await fetchResponse.json();
            return data;
        } catch (e){
            return e;
        }
}
console.log({id}.id);
console.log({info_type}.info_type);
    return(
    <div>
        <form method="post" action="/post" onSubmit={handleSubmit}>
        <input type="text" name={info_type} value={info} onChange={(e)=>setInfo(e.target.value)} placeholder={"Update " + {info_type}.info_type + " Here"} class="textbox" id={info_type}/>
        <input type="submit" className="btn" value="Update"/>
        </form>
    </div>
    );
}

//<text field></> call onChange, update state variable
const UserDetails = ({id}) =>{
    console.log("in UserDetails component: " + id);
    const [userDetails, setUserDetails] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);

    const [username, setUsername] = useState(null);
    const [email, setEmail] = useState(null);
    const [org, setOrg] = useState(null);
    const [address, setAddress] = useState(null);
    const [city, setCity] = useState(null);
    const [locstate, setLocState] = useState(null);
    const [country, setCountry] = useState(null);
    const [postal, setPostal] = useState(null);

    useEffect(() => {
        const fetchUserDetails = async() => {
            setIsLoading(true);
            setError(null);

            try{
                const response = await fetch("http://127.0.0.1:5000/userDetails/" + String(id));
                //something similar to await fetch to do post
                //console.log(response);
                if (!response.ok){
                    throw new Error("failed to fetch details");
                }
                const resp = await response.json();
                const data = JSON.parse(JSON.stringify(resp));
                //console.log(data);
                setUsername(data[0].username);
                setEmail(data[0].email);
                setOrg(data[0].organization);
                setAddress(data[0].address);
                setCity(data[0].city);
                setLocState(data[0].state);
                setCountry(data[0].country);
                setPostal(data[0].postalcode);
            } catch (err) {
                setError(err.message);
            } finally {
                setIsLoading(false);
            }
        };

        if (id) {
            fetchUserDetails();
        }
    }, [id]);

    //console.log(userDetails);

    if (isLoading) return <p>Loading...</p>;
    if (error) return <p>Error: {error}</p>;
    if (!id) return null;

    if (email){
        if (id){
            //console.log(id);
            //console.log(userDetails);
            console.log("email: " + email);
            //console.log(userDetails[0].password);
            //state variables: email, organization, etc, initialize, fill in, will be changed as well
            return(
                <div>
                <p>{username} Details:</p>
                <ul>
                <li> Email: {email}</li><SaveUserDetails id={id} info_type={"email"} /> //info type = userDetails
                <li> Organization: {org}</li><SaveUserDetails id={id} info_type={"organization"} />
                <li> Address: {address}</li><SaveUserDetails id={id} info_type={"address"} />
                <li> City: {city}</li><SaveUserDetails id={id} info_type={"city"} />
                <li> State: {locstate}</li><SaveUserDetails id={id} info_type={"state"} />
                <li> Country: {country}</li><SaveUserDetails id={id} info_type={"country"} />
                <li> Postalcode: {postal}</li><SaveUserDetails id={id} info_type={"postalcode"} />
                </ul>
                </div>
            );
        }
        else {
            return <p></p>;
        }
    }
    else {
        return <p>Click a user to display their details.</p>;
    }
}


const UserList = ({users}) => {
    const [selectedUserId, setSelectedUserId] = useState(null); //usestate [] where it's objects?

    const handleUserClick = (id) => {
        if (selectedUserId && selectedUserId === id){
            setSelectedUserId(null);
        }
        else{
            setSelectedUserId(id);

        }
        console.log(selectedUserId);
        console.log(id);
    };


    //.id or .username?
    return(
        <div>
        <users_border>
        <ul>
            {users.map((user) => (
            <li key={user.id} onClick={() => {handleUserClick(user.id);}}>
                Username: {user.username}
            </li>
            ))}
        </ul>
        </users_border>
        <details_border>
        {selectedUserId && <UserDetails id={selectedUserId}/>}
        </details_border>
        </div>
    );

};


const App = () => {
    const [users, setUsers] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchUsers = async() => {
            try{
                const response = await fetch("http://127.0.0.1:5000/userlist");

                if (!response.ok) {
                    throw new Error('failed to fetch users');
                }
                const data = await response.json();
                console.log(data);
                setUsers(data);
            } catch (err) {
                setError(err.message);
            } finally {
                setIsLoading(false);
            }
        };

        fetchUsers();
    }, []);


    if (isLoading) return <p>Loading...</p>
    if (error) return <p>Error: {error}</p>;

    return <UserList users={users}/>;
};

export default App;
