import "./App.css"
import React from "react";
import {useEffect, useState} from "react";
import axios from "axios";
import TextField from '@mui/material/TextField';

//<text field></> call onChange, update state variable
const UserDetails = ({id}) =>{
    console.log("in UserDetails component: " + id);
    //const [userDetails, setUserDetails] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);

    const [username, setUsername] = useState(null);
    const [email, setEmail] = useState(null);
    const [org, setOrg] = useState(null);
    const [address, setAddress] = useState(null);
    const [city, setCity] = useState(null);
    const [locstate, setLocState] = useState(null);
    const [country, setCountry] = useState(null);
    const [postal, setPostal] = useState(null);

    let userDetails = [
        {username: {username}},
        {email: {email}},
        {organization: {org}},
        {address: {address}},
        {city: {city}},
        {state: {locstate}},
        {country: {country}},
        {postalcode: {postal}},
    ];

    useEffect(() => {
        const fetchUserDetails = async() => {
            setIsLoading(true);
            setError(null);

            try{
                const response = await fetch("http://127.0.0.1:5000/userDetails/" + String(id));
                //something similar to await fetch to do post
                //console.log(response);
                if (!response.ok){
                    throw new Error("failed to fetch details");
                }
                const resp = await response.json();
                const data = JSON.parse(JSON.stringify(resp));
                //console.log(data);
                setUsername(data[0].username);
                setEmail(data[0].email);
                setOrg(data[0].organization);
                setAddress(data[0].address);
                setCity(data[0].city);
                setLocState(data[0].state);
                setCountry(data[0].country);
                setPostal(data[0].postalcode);
            } catch (err) {
                setError(err.message);
            } finally {
                setIsLoading(false);
            }
        };

        if (id) {
            fetchUserDetails();
        }
    }, [id]);

    const handleSubmit = async(event) => {
        event.preventDefault();
        const settings = {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                "id": id,
                username,
                email,
                org,
                address,
                city,
                locstate,
                country,
                postal,
            }),
        };
        try{
            const fetchResponse = await fetch('http://127.0.0.1:5000/saveUserDetails/', settings);
            const data = await fetchResponse.json();
            return data;
        } catch (e){
            return e;
        }
    }

    if (isLoading) return <p>Loading...</p>;
    if (error) return <p>Error: {error}</p>;
    if (!id) return null;

    if (email){
        if (id){
            //console.log(id);
            //console.log(userDetails);
            console.log("email: " + email);
            //console.log(userDetails[0].password);
            //state variables: email, organization, etc, initialize, fill in, will be changed as well
            return(
                <tr>
                <form method="post" action="/post" onSubmit={handleSubmit}>
                    <td><TextField id="email" label="Email:" defaultValue={email} onChange={(e)=>setEmail(e.target.value)}/></td>
                    <td><TextField id="org" label="Organization:" defaultValue={org} onChange={(e)=>setOrg(e.target.value)}/></td>
                    <td><TextField id="address" label="Address:" defaultValue={address} onChange={(e)=>setAddress(e.target.value)}/></td>
                    <td><TextField id="city" label="City:" defaultValue={city} onChange={(e)=>setCity(e.target.value)}/></td>
                    <td><TextField id="locstate" label="State:" defaultValue={locstate} onChange={(e)=>setLocState(e.target.value)}/></td>
                    <td><TextField id="country" label="Country:" defaultValue={country} onChange={(e)=>setCountry(e.target.value)}/></td>
                    <td><TextField id="postal" label="Postalcode:" defaultValue={postal} onChange={(e)=>setPostal(e.target.value)}/></td>
                    <td><input type="submit" className="btn" value="Update"/></td>
                </form>
                </tr>
            );
        }
        else {
            return <p></p>;
        }
    }
    else {
        return <p>Click a user to display their details.</p>;
    }
}


const UserData = ({user}) => {
    console.log("email: " + user.email);
    return (
    <div>
    <table>
    <tbody>
        <td>{user.username}</td>
        <td>{user.email}</td>
        <td>{user.organization}</td>
        <td>{user.address}</td>
        <td>{user.city}</td>
        <td>{user.state}</td>
        <td>{user.country}</td>
        <td>{user.postalcode}</td>
    </tbody>
    </table>
    </div>
    );
}


const UserList = ({users}) => {
    const [selectedUserId, setSelectedUserId] = useState(false);
    const [rowData, setRowData] = useState(users);
    const [editData, setEditData] = useState({});

    const handleUserClick = (id) => {
        if (selectedUserId && selectedUserId === id){
            setSelectedUserId(null);
        }
        else{
            setSelectedUserId(id);
        }
        console.log(selectedUserId);
        console.log(id);
    };

    const getRows = () => {
        return rowData.map((user) => {

            return(
            <tr>
            {selectedUserId===user.id && <UserDetails id={selectedUserId}/>}
                <td>{!(selectedUserId===user.id) && user.username}</td>
                <td>{!(selectedUserId===user.id) && user.email}</td>
                <td>{!(selectedUserId===user.id) && user.organization}</td>
                <td>{!(selectedUserId===user.id) && user.address}</td>
                <td>{!(selectedUserId===user.id) && user.city}</td>
                <td>{!(selectedUserId===user.id) && user.state}</td>
                <td>{!(selectedUserId===user.id) && user.country}</td>
                <td>{!(selectedUserId===user.id) && user.postalcode}</td>
                <td onClick={() => {handleUserClick(user.id);}}>Edit {user.username} data</td>
            </tr>
            );

        });
    }

    //.id or .username?
    return(
        <div>
        <table>
        <thead>
            <tr>
                <th scope="col">Username:</th>
                <th scope="col">Email:</th>
                <th scope="col">Organization:</th>
                <th scope="col">Address:</th>
                <th scope="col">City:</th>
                <th scope="col">State:</th>
                <th scope="col">Country:</th>
                <th scope="col">Postal Code:</th>
                <th scope="col">Edit:</th>
            </tr>
        </thead>
        <tbody>
            {getRows(users)}
        </tbody>
        </table>
        </div>
    );

};


//onclick changes a state variable
//return includes {variable: show form ? hide form}
//show form is another
// addNewUser/
const AddNewUser = () => {
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);

    const [user, setUser] = useState(null);
    const [pass, setPass] = useState(null);
    const [email, setEmail] = useState(null);
    const [org, setOrg] = useState(null);
    const [address, setAddress] = useState(null);
    const [city, setCity] = useState(null);
    const [locstate, setLocState] = useState(null);
    const [country, setCountry] = useState(null);
    const [postal, setPostal] = useState(null);

    let userDetails = [
        {username: {user}},
        {password: {pass}},
        {email: {email}},
        {organization: {org}},
        {address: {address}},
        {city: {city}},
        {state: {locstate}},
        {country: {country}},
        {postalcode: {postal}},
    ];

    //!!!

    const handleSubmit = async(event) => {
        console.log("submit pressed");
        event.preventDefault();
        const settings = {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                user,
                pass,
                email,
                org,
                address,
                city,
                locstate,
                country,
                postal,
            }),
        };
        try{
            const fetchResponse = await fetch('http://127.0.0.1:5000/addNewUser/', settings);
            const data = await fetchResponse.json();
            return data;
        } catch (e){
            return e;
        }
    }

    if (isLoading) return <p>Loading...</p>;
    if (error) return <p>Error: {error}</p>;

    return(
        <div>
        <form method="post" action="/post" onSubmit={handleSubmit}>
            <TextField id="user" label="Username:" onChange={(e)=>setUser(e.target.value)}/>
            <TextField id="pass" label="Password:" onChange={(e)=>setPass(e.target.value)}/>
            <TextField id="email" label="Email:" onChange={(e)=>setEmail(e.target.value)}/>
            <TextField id="org" label="Organization:" onChange={(e)=>setOrg(e.target.value)}/>
            <TextField id="address" label="Address:" onChange={(e)=>setAddress(e.target.value)}/>
            <TextField id="city" label="City:" onChange={(e)=>setCity(e.target.value)}/>
            <TextField id="locstate" label="State:" onChange={(e)=>setLocState(e.target.value)}/>
            <TextField id="country" label="Country:" onChange={(e)=>setCountry(e.target.value)}/>
            <TextField id="postal" label="Postalcode:" onChange={(e)=>setPostal(e.target.value)}/>
            <input type="submit" className="btn" value="Submit"/>
        </form>
        </div>
        );
}

const AddUser = () => {
    const [addUsr, setAddUsr] = useState(false);

    const handleUserClick = () => {
        setAddUsr(!addUsr);
        console.log("add user button pressed");
    }

    return(
    <div>
        <button onClick={handleUserClick} type="button">Add User</button>
        {addUsr && <AddNewUser />}
    </div>
    );
}


const App = () => {
    const [users, setUsers] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchUsers = async() => {
            try{
                const response = await fetch("http://127.0.0.1:5000/userlist");

                if (!response.ok) {
                    throw new Error('failed to fetch users');
                }
                const data = await response.json();
                console.log(data);
                setUsers(data);
            } catch (err) {
                setError(err.message);
            } finally {
                setIsLoading(false);
            }
        };

        fetchUsers();
    }, []);


    if (isLoading) return <p>Loading...</p>
    if (error) return <p>Error: {error}</p>;

    return (
        <div>
        <AddUser />
        <UserList users={users}/>
        </div>
    );
};

export default App;
