import React from "react";
import { useEffect, useState } from "react";
import axios from "axios";
//import userlist from ./userlist

const getUserDesc = () => {
    //takes user's username, calls axios.get for userlist/username
    return(
    <div>Get User Desc</div>
    );
}

const Userlist = () => {

    const [Users, setUsers] = useState([]);
    const [displayInfo, setDisplayInfo] = useState(false);

    React.useEffect(() => {
    //axios.get("http://localhost.5000/userlist").then((response) => {
    axios.get("http://127.0.0.1:5000/userlist").then((response) => {
        console.log(response.data);
        setUsers(response.data);
    });
    });

    getUserDesc = (currentDisplay) => {
        setDisplayInfo(currentDisplay);
    }

    return(
        <div>
        <ul>
            {Users.map(item=> {
                return(
                    <li key={item.id}>
                    <ul>
                        <li>
                            <button onClick={()=> getUserDesc(displayInfo)}>Username: {item.username}</button>
                        </li>
                    </ul>
                    </li>
                );
            })}
        </ul>
        </div>
    );
};

//<li>Password: {item.password}</li>
                        //<li>Email: {item.email}</li>
                        //<li>Organization: {item.organization}</li>
                        //<li>Address: {item.address}</li>
                        //<li>City: {item.city}</li>
                        //<li>State: {item.state}</li>
                        //<li>Country: {item.country}</li>
                        //<li>Postalcode: {item.postalcode}</li>

const App = () => {
    return(
        <div>
        <Userlist/>
        </div>
    );
};

export default App;
