import React from "react";
import {useEffect, useState} from "react";
import axios from "axios";

const UserDetails = ({id}) =>{
    const[userDetails, setUserDetails] = useState(null);

    useEffect(() => {
        const fetchUserDetails = async() => {
            axios.get("http://127.0.0.1:5000/userlist/{id}").then((response) => {
            console.log(response.data);
            setUserDetails(response.data);
            });
        };

        if (id) {
            fetchUserDetails();
        }
    }, [id]);

    if (!id) return null;

    return(
        <div>
        <p>Password: {userDetails.password}</p>
        </div>
    );
}


const UserList = ({users}) => {
    const [selectedUserId, setSelectedUserId] = useState([]); //usestate [] where it's objects?

    const handleUserClick = (id) => {
        setSelectedUserId(id);
    };
    //.id or .username?
    return(
        <div>
        <ul>
        {users.map((user) => (
        <li key={user.username} onClick={() => handleUserClick(user.username)}>
        {user.username}
        </li>
        ))}
        </ul>
        {selectedUserId && <UserDetails id={selectedUserId}/>}
        </div>
    );
};


const App = () => {
    const [users, setUsers] = useState([]);

    useEffect(() => {
        const fetchUsers = async() => {
            axios.get("http://127.0.0.1:5000/userlist").then((response) => {
            console.log(response.data);
            setUsers(response.data);
            });
        };

        fetchUsers();
    }, []);

    return <UserList users={users}/>;
};

export default App;
