import React from "react";
import { useEffect, useState } from "react";
import axios from "axios";
//import userlist from ./userlist

const UserDetails = (id) => {
    const [Details, setDetails] = useState([]); //might be unnecessary

    React.useEffect(() => {
        axios.get("http://127.0.0.1:5000/", id).then((response) => {
            console.log(response.data);
            setDetails(response.data);
        });
    });

    return Details;
}

const Userlist = () => {

    const [Users, setUsers] = useState([]);
    const [displayInfo, setDisplayInfo] = useState('');

    React.useEffect(() => {
    //axios.get("http://localhost.5000/userlist").then((response) => {
    axios.get("http://127.0.0.1:5000/userlist").then((response) => {
        console.log(response.data);
        setUsers(response.data);
    });
    });

    const handleClick = () => {
        setDisplayInfo(displayInfo === 'No info' ? 'Info' : 'No info');
    };

    return(
        <div>
        <ul>
            {Users.map(item=> {
                return(
                    <li key={item.id}>
                    <ul>
                        <li>
                            Username:{item.username}
                            <button onClick={handleClick}>Display User Details</button>
                            <p>{displayInfo}</p>
                        </li>
                    </ul>
                    </li>
                );
            })}
        </ul>
        </div>
    );
};

//<li>Password: {item.password}</li>
                        //<li>Email: {item.email}</li>
                        //<li>Organization: {item.organization}</li>
                        //<li>Address: {item.address}</li>
                        //<li>City: {item.city}</li>
                        //<li>State: {item.state}</li>
                        //<li>Country: {item.country}</li>
                        //<li>Postalcode: {item.postalcode}</li>

const App = () => {
    return(
        <div>
        <Userlist/>
        </div>
    );
};

export default App;
