const SaveUserDetails = ({id}) =>{
    const [email, setEmail] = useState("");

    const SubmitDetails = ({given}) => {
    console.log(given);
    const updateEmail = given.target.querySelector('input[type="email]').value;
    console.log(updateEmail);

    if (updateEmail){
        const saveUserDetails = async() =>{
            const settings = {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                }
            };
            try{
                const fetchResponse = await fetch('http://127.0.0.1:5000/saveUserDetails/', settings)
                const data = await fetchResponse.json();
                return data;
            } catch (e){
                //e.preventDefault();
                return e;
            }
        }
    }
};

    return(
    <div>
        <form action="#" onSubmit={SubmitDetails}>
        <input type="text" name="email" value={email} onChange={(e)=>setEmail(e.target.value)} placeholder="Update Email Here" class="textbox" id="email"/>
        <input type="submit" className="btn" value="Update"/>
        </form>
    </div>
    );
}


const UserDetails = ({id}) =>{
    console.log("in UserDetails component: " + id);
    const [userDetails, setUserDetails] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchUserDetails = async() => {
            setIsLoading(true);
            setError(null);

            try{
                const response = await fetch("http://127.0.0.1:5000/userDetails/" + String(id));
                //something similar to await fetch to do post
                //console.log(response);
                if (!response.ok){
                    throw new Error("failed to fetch details");
                }
                const data = await response.json();
                //console.log(data);
                setUserDetails(data);
            } catch (err) {
                setError(err.message);
            } finally {
                setIsLoading(false);
            }
        };

        if (id) {
            fetchUserDetails();
        }
    }, [id]);

    //console.log(userDetails);

    if (isLoading) return <p>Loading...</p>;
    if (error) return <p>Error: {error}</p>;
    if (!id) return null;

    if (userDetails){
        if (id){
            console.log(id);
            console.log(userDetails);
            //console.log(userDetails[0].password);
            return(
                <div>
                <p>{userDetails[0].username} Details:</p>
                <ul>
                <li> Email: {userDetails[0].email}</li><SaveUserDetails id={id} />
                <li> Organization: {userDetails[0].organization}</li>
                <li> Address: {userDetails[0].address}</li>
                <li> City: {userDetails[0].city}</li>
                <li> State: {userDetails[0].state}</li>
                <li> Country: {userDetails[0].country}</li>
                <li> Postalcode: {userDetails[0].postalcode}</li>
                </ul>
                </div>
            );
        }
        else {
            return <p></p>;
        }
    }
    else {
        return <p>Click a user to display their details.</p>;
    }
}


const UserList = ({users}) => {
    const [selectedUserId, setSelectedUserId] = useState(null); //usestate [] where it's objects?

    const handleUserClick = (id) => {
        if (selectedUserId && selectedUserId === id){
            setSelectedUserId(null);
        }
        else{
            setSelectedUserId(id);

        }
        console.log(selectedUserId);
        console.log(id);
    };


    //.id or .username?
    return(
        <div>
        <users_border>
        <ul>
            {users.map((user) => (
            <li key={user.id} onClick={() => {handleUserClick(user.id);}}>
                Username: {user.username}
            </li>
            ))}
        </ul>
        </users_border>
        <details_border>
        {selectedUserId && <UserDetails id={selectedUserId}/>}
        </details_border>
        </div>
    );

};


const App = () => {
    const [users, setUsers] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchUsers = async() => {
            try{
                const response = await fetch("http://127.0.0.1:5000/userlist");

                if (!response.ok) {
                    throw new Error('failed to fetch users');
                }
                const data = await response.json();
                console.log(data);
                setUsers(data);
            } catch (err) {
                setError(err.message);
            } finally {
                setIsLoading(false);
            }
        };

        fetchUsers();
    }, []);


    if (isLoading) return <p>Loading...</p>
    if (error) return <p>Error: {error}</p>;

    return <UserList users={users}/>;
};

export default App;
