import React from "react";
import { useEffect, useState } from "react";
import axios from "axios";

const UserDetails = ({ username }) => {
    const [userDetails, setUserDetails] = useState(null);
    //loading
    //error

    useEffect(() => {
        const fetchUserDetails = async () => {
            axios.get("http://127.0.0.1:5000/userlist/{username}").then((response) => {
            console.log(response.data);
            setUserDetails(response.data)
        })
    }

    if (username){
        fetchUserDetails();
    }
    }, [username]);

    return(
    <div>
    <p>Password: {UserDetails.password}</p>
    <p>Email: {UserDetails.email}</p>
    <p>Organization: {UserDetails.organization}</p>
    <p>Address: {UserDetails.address}</p>
    <p>City: {UserDetails.city}</p>
    <p>State: {UserDetails.state}</p>
    <p>Country: {UserDetails.country}</p>
    <p>Postalcode: {UserDetails.postalcode}</p>
    </div>
    );
};

const Userlist = ({users}) => {
    const [selectedUser, setSelectedUser] = useState(null);

    const handleUserClick = (username) => {
        setSelectedUser(username);
    };

    return(
    <div>
    <ul>
        {users.map((user) => (
            <li key={user.id} onClick={() => handleUserClick(user.id)}>
                {user.name}
            </li>
        ))}
    </ul>
    {selectedUser && <UserDetails username={selectedUser}/>}
    </div>
    );
};

const App = () => {
    const [Users, setUsers] = useState([]);

    React.useEffect(() => {
    //axios.get("http://localhost.5000/userlist").then((response) => {
    axios.get("http://127.0.0.1:5000/userlist").then((response) => {
        console.log(response.data);
        setUsers(response.data);
    });
    });//, []);

    return <Userlist users={Users}/>;
};

export default App;
