import React from "react";
import {useEffect, useState} from "react";
//import axios from "axios";

const UserDetails = ({id}) =>{
    const [userDetails, setUserDetails] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchUserDetails = async() => {
            setIsLoading(true);
            setError(null);

            try{
                const response = await fetch("http://127.0.0.1:5000/userlist/${id}");

                if (!response.ok){
                    throw new Error("failed to fetch details");
                }

                const data = await response.json();
                setUserDetails(data);
            } catch (err) {
                setError(err.message);
            } finally {
                setIsLoading(false);
            }
        };

        if (id) {
            fetchUserDetails();
        }
    }, [id]);

    //console.log(userDetails);

    if (isLoading) return <p>Loading...</p>;
    if (error) return <p>Error: {error}</p>;
    if (!id) return null;

    return(
        <div>
        <p>Password: {userDetails.password}</p>
        </div>
    );
}


const UserList = ({users}) => {
    const [selectedUserId, setSelectedUserId] = useState(null); //usestate [] where it's objects?

    const handleUserClick = (id) => {
        setSelectedUserId(id);
        console.log(selectedUserId);
    };
    //.id or .username?
    return(
        <div>
        <ul>
            {users.map((user) => (
            <li key={user.id} onClick={() => handleUserClick(user.id)}>
                {user.username}
            </li>
            ))}
        </ul>
        {selectedUserId && <UserDetails id={selectedUserId}/>}
        </div>
    );
};


const App = () => {
    const [users, setUsers] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchUsers = async() => {
            try{
                const response = await fetch("http://127.0.0.1:5000/userlist");

                if (!response.ok) {
                    throw new Error('failed to fetch users');
                }
                const data = await response.json();
                console.log(data);
                setUsers(data);
            } catch (err) {
                setError(err.message);
            } finally {
                setIsLoading(false);
            }
        };

        fetchUsers();
    }, []);


    if (isLoading) return <p>Loading...</p>
    if (error) return <p>Error: {error}</p>;

    return <UserList users={users}/>;
};

export default App;
