import "./App.css"
import React from "react";
import {useEffect, useState} from "react";
import axios from "axios";
//import { TextField } from "@mui/material/TextField";

const SaveUserDetails = ({id, data}) =>{
    const [info, setInfo] = useState("");

      const handleSubmit = async(event) => {
        event.preventDefault();
        //alert('entered: ${email}');
        console.log("data is " + JSON.stringify({data}));
        console.log("info type is " + info_type);
        console.log(JSON.stringify({"id": id, data}));
        const settings = {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                "id": id,
                data,
            }),
        };
        try{
            const fetchResponse = await fetch('http://127.0.0.1:5000/saveUserDetails/', settings);
            const retdata = await fetchResponse.json();
            return retdata;
        } catch (e){
            return e;
        }
}
console.log({id}.id);
console.log({info_type}.info_type);

return;
}

//<text field></> call onChange, update state variable

//possible:
//in returns is text field with the onChange that updates state variable
//updating the state variable is in a separate function that includes the call to userDetails that current UserDetails has
//???

//or see how codecademy html forms did its thing...
//do this:
//<text> info: {info} </>
//<form method>
//<text><text input name>
// ...
//<text> last info: {last info}</>
//<form method>
//<text><text input name>
//<submit>
//</form>
//each form method onsubmit/onchange will call method w current saveuserdetails stuff

//IF SOMETHING BREAKS:
//maybe onChange->useEffect in UserDetails isn't valid
//onSubmit in form, see if its ok to have the {{}}
//see if onSubmit should be in <form> or in <input type>
//does data passed to SaveUserDetails 1. have the info 2. stay updated w updated info
//can SaveUserDetails get away w/o returning something of substance

const UserDetails = ({id}) =>{
    console.log("in UserDetails component: " + id);
    const [userDetails, setUserDetails] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);

    const [username, setUsername] = useState(null);
    const [email, setEmail] = useState(null);
    const [org, setOrg] = useState(null);
    const [address, setAddress] = useState(null);
    const [city, setCity] = useState(null);
    const [locstate, setLocState] = useState(null);
    const [country, setCountry] = useState(null);
    const [postal, setPostal] = useState(null);

    useEffect(() => {
        const fetchUserDetails = async() => {
            setIsLoading(true);
            setError(null);

            try{
                const response = await fetch("http://127.0.0.1:5000/userDetails/" + String(id));
                //something similar to await fetch to do post
                //console.log(response);
                if (!response.ok){
                    throw new Error("failed to fetch details");
                }
                const data = await response.json();
                //console.log(data);
                setUserDetails(data);
                setUsername(userDetails[0].username);
                setEmail(userDetails[0].email);
                setOrg(userDetails[0].organization);
                setAddress(userDetails[0].address);
                setCity(userDetails[0].city);
                setLocState(userDetails[0].state);
                setCountry(userDetails[0].country);
                setPostal(userDetails[0].postalcode);
            } catch (err) {
                setError(err.message);
            } finally {
                setIsLoading(false);
            }
        };

        if (id) {
            fetchUserDetails();
        }
    }, [id]);

    //console.log(userDetails);

    if (isLoading) return <p>Loading...</p>;
    if (error) return <p>Error: {error}</p>;
    if (!id) return null;

    if (userDetails){
        if (id){
            console.log(id);
            console.log(userDetails);
            //console.log(userDetails[0].password);
            //state variables: email, organization, etc, initialize, fill in, will be changed as well
            return(
                <div>
                <p>{username} Details:</p>
                <form method="post" action="/post" onSubmit={SaveUserDetails(id, data)}>
                    <br/>
                    <label for="email">Email: {email}</label> //onChange, need to change whatevers in {}
                    <input id="email" type="text" name="email" onChange={useEffect} />
                    <br/>
                    <label for="org">Organization: {org}</label>
                    <input id="org" type="text" name="org" onChange={useEffect} />
                    <br/>
                    <label for="address">Address: {address}</label>
                    <input id="address" type="text" name="address" onChange={useEffect} />
                    <br/>
                    <label for="city">City: {city}</label>
                    <input id="city" type="text" name="city" onChange={useEffect} />
                    <br/>
                    <label for="locstate">State: {locstate}</label>
                    <input id="locstate" type="text" name="locstate" onChange={useEffect} />
                    <br/>
                    <label for="country">Country: {country}</label>
                    <input id="country" type="text" name="country" onChange={useEffect} />
                    <br/>
                    <label for="postal">Postalcode: {postal}</label>
                    <input id="postal" type="text" name="postal" onChange={useEffect} />
                    <input type="submit" value="Update Info" />
                </form>
                </div>
            );
        }
        else {
            return <p></p>;
        }
    }
    else {
        return <p>Click a user to display their details.</p>;
    }
}


const UserList = ({users}) => {
    const [selectedUserId, setSelectedUserId] = useState(null); //usestate [] where it's objects?

    const handleUserClick = (id) => {
        if (selectedUserId && selectedUserId === id){
            setSelectedUserId(null);
        }
        else{
            setSelectedUserId(id);

        }
        console.log(selectedUserId);
        console.log(id);
    };


    //.id or .username?
    return(
        <div>
        <users_border>
        <ul>
            {users.map((user) => (
            <li key={user.id} onClick={() => {handleUserClick(user.id);}}>
                Username: {user.username}
            </li>
            ))}
        </ul>
        </users_border>
        <details_border>
        {selectedUserId && <UserDetails id={selectedUserId}/>}
        </details_border>
        </div>
    );

};


const App = () => {
    const [users, setUsers] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchUsers = async() => {
            try{
                const response = await fetch("http://127.0.0.1:5000/userlist");

                if (!response.ok) {
                    throw new Error('failed to fetch users');
                }
                const data = await response.json();
                console.log(data);
                setUsers(data);
            } catch (err) {
                setError(err.message);
            } finally {
                setIsLoading(false);
            }
        };

        fetchUsers();
    }, []);


    if (isLoading) return <p>Loading...</p>
    if (error) return <p>Error: {error}</p>;

    return <UserList users={users}/>;
};

export default App;
