import React from "react";
import { useEffect, useState } from "react";
import axios from "axios";
//import userlist from ./userlist

const GetUserDetails = (username) => {
    const [userDetails, setUserDetails] = useState('');
    React.useEffect(() => {
        //axios.get("http://localhost.5000/userlist").then((response) => {
        axios.get("http://127.0.0.1:5000/userlist/{username}").then((response) => {
        console.log(response.data);
        setUserDetails(response.data)
    });
    });

    return userDetails;
}

function UserDetailsWrapper({ setState }){
    const details = GetUserDetails();

    useEffect(() => {
        setState(details);
    }, [setState, details]);

    return null;
}

const UserDetails = (username) => {
    const [displayInfo, setDisplayInfo] = useState('');
    const [isHookActive, setIsHookActive] = useState(false);

    return(
        <div>
            <button onClick={() => setIsHookActive(!isHookActive)}>{JSON.stringify(username)} details</button>
            <p>{isHookActive ? 'details: ${displayInfo}': 'no details'}</p>
            {isHookActive && <UserDetailsWrapper setState={displayInfo} />}
        </div>
    );
}

const Userlist = () => {

    const [Users, setUsers] = useState([]);

    React.useEffect(() => {
    //axios.get("http://localhost.5000/userlist").then((response) => {
    axios.get("http://127.0.0.1:5000/userlist").then((response) => {
        console.log(response.data);
        setUsers(response.data);
    });
    });

    return(
        <div>
        <ul>
            {Users.map(item=> {
                return(
                    <li key={item.id}>
                    <ul>
                        <li>
                            <UserDetails username={JSON.stringify(item.username)}/>
                        </li>
                    </ul>
                    </li>
                );
            })}
        </ul>
        </div>
    );
};

//<li>Password: {item.password}</li>
                        //<li>Email: {item.email}</li>
                        //<li>Organization: {item.organization}</li>
                        //<li>Address: {item.address}</li>
                        //<li>City: {item.city}</li>
                        //<li>State: {item.state}</li>
                        //<li>Country: {item.country}</li>
                        //<li>Postalcode: {item.postalcode}</li>

const App = () => {
    return(
        <div>
        <Userlist/>
        </div>
    );
};

export default App;
