import React from "react";
import { useEffect, useState } from "react";
import axios from "axios";
//import userlist from ./userlist

const UserDetails = (username) => {
    const [displayInfo, setDisplayInfo] = useState('Display user info');

    const handleClick = () => {
        if (displayInfo === 'Display user info'){
            setDisplayInfo('info');
        }
        else{
            setDisplayInfo('Display user info');
        }
    };

    return(
    <div>
        <button onClick={handleClick}>{JSON.stringify(username)} details</button>
        <p>{displayInfo}</p>
        </div>
    );
    //return <button>not clicked</button>;
}

const Userlist = () => {

    const [Users, setUsers] = useState([]);

    React.useEffect(() => {
    //axios.get("http://localhost.5000/userlist").then((response) => {
    axios.get("http://127.0.0.1:5000/userlist").then((response) => {
        console.log(response.data);
        setUsers(response.data);
    });
    });

    return(
        <div>
        <ul>
            {Users.map(item=> {
                return(
                    <li key={item.id}>
                    <ul>
                        <li>
                            <UserDetails username={JSON.stringify(item.username)}/>
                        </li>
                    </ul>
                    </li>
                );
            })}
        </ul>
        </div>
    );
};

//<li>Password: {item.password}</li>
                        //<li>Email: {item.email}</li>
                        //<li>Organization: {item.organization}</li>
                        //<li>Address: {item.address}</li>
                        //<li>City: {item.city}</li>
                        //<li>State: {item.state}</li>
                        //<li>Country: {item.country}</li>
                        //<li>Postalcode: {item.postalcode}</li>

const App = () => {
    return(
        <div>
        <Userlist/>
        </div>
    );
};

export default App;
