import React from "react";
import { useEffect, useState } from "react";
import axios from "axios";
//import userlist from ./userlist

const UserDetails = ({username}) => {
    const [displayInfo, setDisplayInfo] = useState('');

    useEffect(() => {
        const fetchUserDetails = async () => {
            axios.get("http://127.0.0.1:5000/userlist/{username}").then((response) => {
            console.log(response.data);
            setDisplayInfo(response.data)
        })};

        if (username){
            fetchUserDetails();
        }
    }, [username]);

    return(
    <div>
    <p>Password: {displayInfo.password}</p>
    <p>Email: {displayInfo.email}</p>
    <p>Organization: {displayInfo.organization}</p>
    <p>Address: {displayInfo.address}</p>
    <p>City: {displayInfo.city}</p>
    <p>State: {displayInfo.state}</p>
    <p>Country: {displayInfo.country}</p>
    <p>Postalcode: {displayInfo.postalcode}</p>
    </div>
    );
};

const Userlist = () => {

    const [Users, setUsers] = useState([]);
    const [selectedUser, setSelectedUser] = useState(null);

    React.useEffect(() => {
    //axios.get("http://localhost.5000/userlist").then((response) => {
    axios.get("http://127.0.0.1:5000/userlist").then((response) => {
        console.log(response.data);
        setUsers(response.data);
    });
    });

    const handleUserClick = (username) => {
        setSelectedUser(username);
    };

    return(
        <div>
        <ul>
            {Users.map(item=> {
                return(
                    <li key={item.id}>
                    <ul>
                        <li>
                            <button onClick={handleUserClick(username)}> details</button>
                        </li>
                    </ul>
                    </li>
                );
            })}
        </ul>
        {selectedUser && <UserDetails username={selectedUser}/>}
        </div>
    );
};

//<li>Password: {item.password}</li>
                        //<li>Email: {item.email}</li>
                        //<li>Organization: {item.organization}</li>
                        //<li>Address: {item.address}</li>
                        //<li>City: {item.city}</li>
                        //<li>State: {item.state}</li>
                        //<li>Country: {item.country}</li>
                        //<li>Postalcode: {item.postalcode}</li>

const App = () => {
    return(
        <div>
        <Userlist/>
        </div>
    );
};

export default App;
